    textbox.parentNode.height = parseInt(textbox.parentNode.minHeight);
    convTopHeight = parseInt(convTopStyle.getPropertyValue("height"));
    textbox.parentNode.height = parseInt(textbox.parentNode.minHeight) +
                                (convTopHeight - convTopMinHeight);
  }
}

